mapper.select:
delete
from HEART_T 
where BOARD_IDX = #{BOARD_IDX}
and MEM_IDX = #{MEM_IDX}

select m.MEM_ID as BRD_WRT_ID, f.COMT_IDX, f.COMT_SYSDATE, f.BRD_IDX, f.COMT_CONTENT
from COMMENT_T as f join MEMBER_T as m on f.MEM_IDX = m.MEM_IDX
where BRD_IDX = #{num}

select MEM_ID from
MEMBER_T
where MEM_IDX = #{usernum}

mapper.update:
update BOARD_T set
BRD_TIT = #{BRD_TIT},
BRD_CONTENT = #{BRD_CONTENT},
CATEGORY_IDX = #{CATEGORY_IDX}
where BRD_IDX = #{BRD_IDX}
